mapper.select:
SELECT t.id AS id, t.category AS category, t.comparison_price AS comparisonPrice, t.note AS note, t.ranges AS ranges, t.specifications AS specifications, t.unit AS unit FROM pcms_supplier_material t
t.category LIKE '%${params.category}%'
OR t.specifications LIKE '%${params.specifications}%'
AND t.company = #{params.company,jdbcType=VARCHAR}
AND t.version = #{params.version,jdbcType=VARCHAR}
AND t.vendor_id = #{params.vendor_id,jdbcType=VARCHAR}
ORDER BY t.create_time DESC
