mapper.select:
SELECT DISTINCT tpr.request_id requestId, tpr.request_title requestTitle, td.org_name requestDeptName, tpr.request_company_code companyCode, tpr.request_create_time requestCreateTime, tpr.request_end_time requestEndTime, tee.person_name requestUserName FROM tpm_project tpr LEFT JOIN tpm_activity tac ON ( tac.request_id = tpr.request_id ) LEFT JOIN tpm_activity_original tao ON ( tac.project_id = tao.project_id ) LEFT JOIN tpm_dept td ON ( tpr.request_dept = td.org_code ) LEFT JOIN tpm_employee tee ON ( tpr.request_user = tee.person_code )
#{item,jdbcType=VARCHAR}
AND tac.manager = #{personCode,jdbcType=VARCHAR}
AND ( tac.manager = #{personCode,jdbcType=VARCHAR} OR tpr.request_user = #{personCode,jdbcType=VARCHAR} )
AND tpr.request_company_code LIKE #{params.companyCode,jdbcType=VARCHAR} ESCAPE '/'
AND td.org_name LIKE #{params.requestDeptName,jdbcType=VARCHAR} ESCAPE '/'
AND tee.person_name LIKE #{params.requestUserName,jdbcType=VARCHAR} ESCAPE '/'
AND tpr.request_Id LIKE #{params.requestId,jdbcType=VARCHAR} ESCAPE '/'
AND tpr.request_title LIKE #{params.requestTitle,jdbcType=VARCHAR} ESCAPE '/'
AND tac.activity_name LIKE #{params.projectName,jdbcType=VARCHAR} ESCAPE '/'
AND tpr.request_create_time >= #{params.startTime1,jdbcType=VARCHAR}
AND tpr.request_create_time <= #{params.endTime1,jdbcType=VARCHAR}
AND tpr.request_end_time >= #{params.startTime2,jdbcType=VARCHAR}
AND tpr.request_end_time <= #{params.endTime2,jdbcType=VARCHAR}
AND tao.city LIKE #{params.city,jdbcType=VARCHAR} ESCAPE '/'
ORDER BY tpr.request_create_time DESC

SELECT count(1) FROM tpm_user_activity tua, tpm_activity tac, tpm_project tpr WHERE tua.activity_uuid = tac.activity_uuid AND tpr.request_id = tac.request_id AND tac.activity_uuid = #{uuid,jdbcType=VARCHAR} AND tua.openid = #{openid,jdbcType=VARCHAR} AND tua.request_activity_day=#{requestActivityDay} AND tua.reject_flag!=1
AND tac.manager = #{personCode,jdbcType=VARCHAR}
AND ( tac.manager = #{personCode,jdbcType=VARCHAR} OR tpr.request_user = #{personCode,jdbcType=VARCHAR} )

SELECT tpr.request_id requestId, tac.activity_uuid activityUuid, tac.activity_name projectName, tpr.request_company_code companyCode, tpr.request_create_time requestCreateTime, td2.org_name requestDeptName, td.org_name deptName, tac.city, tee.person_name managerName, tac.start_time startTime, tac.end_time endTime, CASE WHEN tac.start_time > CURDATE() THEN 0 WHEN ( tac.start_time <= CURDATE() AND tac.end_time >= CURDATE() ) THEN 1 ELSE 2 END activityState, tac.user_no userNo, tac.total_day totalDay, tac.work_hours workHours, tac.settlement_cycle settlementCycle, tac.settlement_method settlementMethod, tao.fee_detail_type feeDetailType, tao.cost_center costCenter, tac.incentive_amount incentiveAmount, tac.unit_price unitPrice, tac.district, tao.order_no orderNo, tee2.person_name requestUserName, tee2.mobile requestTelphone, tpr.request_info requestReasons, tao.note FROM tpm_activity tac LEFT JOIN tpm_project tpr ON ( tac.request_id = tpr.request_id ) LEFT JOIN tpm_activity_original tao ON ( tac.project_id = tao.project_id ) INNER JOIN tpm_dept td ON (tao.dept = td.org_code) INNER JOIN tpm_dept td2 ON ( tpr.request_dept = td2.org_code ) INNER JOIN tpm_employee tee ON ( tac.manager = tee.person_code ) INNER JOIN tpm_employee tee2 ON ( tpr.request_user = tee2.person_code )
#{item,jdbcType=VARCHAR}
AND tac.manager = #{personCode,jdbcType=VARCHAR}
AND ( tac.manager = #{personCode,jdbcType=VARCHAR} OR tpr.request_user = #{personCode,jdbcType=VARCHAR} )
AND tac.activity_uuid = #{uuid,jdbcType=VARCHAR}

SELECT
count(1)
FROM
tpm_user_activity tua
WHERE
tua.activity_uuid = #{activityUuid}
AND tua.request_state!=0
AND tua.request_activity_day=#{activityDay}
And tua.openid = #{openid}

SELECT
count(1)
FROM
tpm_user_activity tua
WHERE
tua.activity_uuid = #{activityUuid}
AND tua.request_state=0
AND tua.request_activity_day=#{requestActivityDay}
AND tua.openid = #{openid}

SELECT
request_state
FROM
tpm_user_activity tua
WHERE
tua.activity_uuid = #{activityUuid}
AND tua.request_activity_day=#{requestActivityDay}
AND tua.openid = #{openid}

SELECT
reason
FROM
tpm_user_activity tua
WHERE
tua.request_activity_day =
#{activityDay}
AND tua.activity_uuid = #{activityUuid}
AND tua.openid =
#{openid}

SELECT *
FROM tpm_activity
WHERE activity_uuid=#{activityUuid}
